import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { User } from '../../service/requestTypes/user';
import { LoginuserService } from '../../service/login-svc/loginuser.service';
import { LoginResponse } from '../../responses/LoginResponse';

@Component({
  selector: 'app-user-login',
  templateUrl: './user-login.component.html',
  styleUrls: ['./user-login.component.css'],
})
export class UserLoginComponent {
  user: User = new User();

  constructor(
    private loginuserservice: LoginuserService,
    private router: Router
  ) {}

  ngOnInit(): void {}

  userLogin() {
    console.log(this.user);
    this.loginuserservice.loginUser(this.user).subscribe(
      ( data: LoginResponse ) => {


        //console.log(data);
        console.log("token \n");
        console.log(data.token);  
        console.log("\n");
        localStorage.setItem('auth_token', data.token);


        alert('login successful');
        this.router.navigate(['/home']);
      },
      (error) => alert('invalid credentials')
    );
  }
}
<div class="container">
  <h1>ConnectingMinds</h1>
  <h2>Login</h2>
  <form (ngSubmit)="userLogin()">
    <div class="form-group">
      <label for="exampleInputUsername">Username</label>
      <input
        type="text"
        class="form-control"
        id="username"
        aria-describedby="usernameHelp"
        placeholder="Enter username"
        [(ngModel)]="user.username" 
        name="username" />
    </div>
    <div class="form-group">
      <label for="exampleInputPassword">Password</label>
      <input
        type="password"
        [(ngModel)]="user.password"
        class="form-control"
        id="password"
        placeholder="Password"
        name="password"
      />
    </div>
    <button type="submit" class="btn btn-primary">Login</button>
  </form>
  <hr />
  <div class="text-center">
    <p>Don't have an account yet?</p>
    <button class="btn btn-link" routerLink="/register">Register Here</button>
  </div>
  <hr />
  <div class="text-center">
    <p>if you don t remember your password </p>
    <button class="btn btn-link" routerLink="/register"> Forgot password </button>
  </div>

</div>
